'use strict';

var AuditlogServiceTransport = require('./ServiceTransport');
var ConsoleTransport = require('./ConsoleTransport');

function resolve(credentials, options, securityContext) {
  if (credentials.logToConsole === true) {
    return new ConsoleTransport();
  }

  return new AuditlogServiceTransport(credentials, options, securityContext);
}

module.exports = resolve;
