'use strict';

const _ = require('lodash');

exports.DEFAULT_LEVELS = {
  LOGGER: 'info',
  TRACER: 'error'
};

exports.LOG_LEVELS = {
  info: 2,
  warning: 3,
  error: 4,
  fatal: 5
};

exports.TRACE_LEVELS = _.extend({
  debug: 0,
  path: 1
}, exports.LOG_LEVELS);

exports.ALL_LEVELS = exports.TRACE_LEVELS;

exports.MOST_VERBOSE_LEVEL = {
  LOGGER: exports.DEFAULT_LEVELS.LOGGER
};

exports.NEW_LINE_SEPARATOR = '{xsa.newlineSeparator}';

exports.LIB_CATEGORY = '/LoggingLibrary';
exports.NETWORK_LOG_CATEGORY = exports.LIB_CATEGORY + '/NetworkLog';
